mapper.insert:
INSERT INTO admin(
		 member_id memberId
		,admin_name adminName
		,admin_phone adminPhone
		,admin_address adminAddress
		,admin_detailaddress adminDetailAddress
		,admin_email adminEmail
		,update_date updateDate
	) VALUES (
		 #{memberId}
		,#{adminName}
		,#{adminPhone}
		,#{adminAddress}
		,#{adminDetailAddress}
		,#{adminEmail}
		,#{updateDate}
	)

mapper.update:
UPDATE admin SET
		 member_id = #{memberId}
		,admin_name = #{adminName}
		,admin_phone = #{adminPhone}
		,admin_address = #{adminAddress}
		,admin_detailaddress = #{adminDetailAddress}
		,admin_email = #{adminEmail}
		,update_date = NOW()
WHERE member_id = #{memberId}
