mapper.insert:
INSERT INTO student (
	 member_id memberId
	,student_name studentName
	,student_gender studentGender
	,student_phone studentPhone
	,student_address studentAddress
	,student_detailaddress studentDetailaddress
	,student_email studentEmail
	,student_education studentEducation
	,student_register_date studentRegisterDate
	,update_date updateDate
) VALUES (
	 #{memberId}
	,#{studentName}
	,#{studentGender}
	,#{studentPhone}
	,#{studentAddress}
	,#{studentDetailaddress}
	,#{studentEmail}
	,#{studentEducation}
	,#{studentRegisterDate}
	,#{updateDate}
)

INSERT INTO subject_student (
	 member_id memberId
	,subject_no subjectNo
	,update_date updateDate
) VALUES (
	 #{memberId}
	,#{subjectNo}
	,#{updateDate}
)
