mapper.select:
SELECT b.board_no boardNo ,b.member_id memberId ,t.teacher_name teacherName ,s.student_name studentName ,b.board_title boardTitle ,b.private_no privateNo ,b.create_date createDate ,b.update_date updateDate FROM board b LEFT JOIN teacher t ON t.member_id = b.member_id LEFT JOIN student s ON s.member_id = b.member_id WHERE board_category = "3"
AND CONCAT('%', b.board_title, '%') LIKE CONCAT('%', #{searchInquiryTitle}, '%')
ORDER BY create_date desc LIMIT #{beginRow} , #{rowPerPage}

mapper.insert:

	select last_insert_id()

INSERT INTO board(
		member_id
	   ,board_category
	   ,board_title
	   ,board_content
	   ,private_no
	   ,create_date
	   ,update_date
) VALUES (
		#{memberId}
	   ,"3"
	   ,#{boardTitle}
	   ,#{boardContent}
	   ,#{privateNo}
	   ,NOW()
	   ,NOW()
	)

mapper.update:
UPDATE board
   SET board_title = #{boardTitle}
      ,board_content = #{boardContent}
      ,private_no = #{privateNo}
      ,update_date = NOW()
 WHERE board_no = #{boardNo}
